<template>
  <div class="pb-20">
    <div class="flex">
      <div class="mr-6">
        <Image
          :width="300"
          style="flex-shrink: 0"
          :src="detailInfo.pic"
          fallback="data:image/png;base64,"
        />
      </div>
      <OrderInfo class="flex-1" :detailInfo="detailInfo" title="订单信息" />
    </div>

    <div class="relative">
      <div class="orderListBtn flex-row">
        <a-button
          class="mr-4"
          v-if="hasPermission('OrderDetailUploadContract') && detailInfo.status == 301"
          type="primary"
          @click="handleOrderUploadContractModal"
        >
          已签纸质合同
        </a-button>
        <a-button
          class="mr-4"
          v-if="hasPermission('OrderDetailBindCard')"
          type="primary"
          @click="handleBindCard"
        >
          查看绑卡信息
        </a-button>
        <a-button
          v-if="hasPermission('OrderDetailEditModal') && isHistory"
          type="primary"
          @click="handleOrderListModal"
        >
          历史订单
        </a-button>
      </div>
      <Description
        size="middle"
        title="用户信息"
        :bordered="false"
        :column="3"
        :data="personData"
        :schema="personSchema"
      />
    </div>
    <div
      class="flex"
      title="实名认证信息"
      v-if="personData.cardFrontImage && hasPermission('orderDetail_idCard')"
    >
      <div>
        <Image
          :width="200"
          :src="personData.cardFrontImage"
          fallback="data:image/png;base64,"
        />
      </div>
      <div style="margin-left: 20px" v-if="personData.cardBackImage">
        <Image
          :width="200"
          :src="personData.cardBackImage"
          fallback="data:image/png;base64,"
        />
      </div>
      <div style="margin-left: 20px" v-if="personData.cardHandImage">
        <Image
          :width="200"
          style="margin-left: 40px"
          :src="personData.cardHandImage"
          fallback="data:image/png;base64,"
        />
      </div>
      <div
        style="margin-left: 20px"
        v-for="(item, index) in personData.lipReadingDetectionList"
        :key="index"
      >
        <video title="活体视频" controls style="width: 200px" :src="item"></video>
      </div>
    </div>
    <a-divider />
    <template v-if="hasPermission('orderDtail_ContactsTable')">
      <ContactsTable :pdata="contact" key="detail" />
    </template>
    <OrderHistoryModal @register="orderHistoryRegister" />
    <BindCardModel @register="bindCardRegister" @success="handleSuccess" />
    <UserWorkModel @register="userWorkRegister" @success="handleSuccess" />
    <UploadContractModal @register="uploadContractRegister" @success="handleSuccess" />
  </div>
</template>
<script lang="ts">
  import { defineComponent, h, inject, ref } from 'vue'
  import { Description } from '/@/components/Description/index'
  import { Divider, Image, Modal } from 'ant-design-vue'
  import { personSchema } from './data'
  import { formatNumber } from '/@/utils/tool'
  import { platform } from '../../../utils'
  import OrderInfo from './components/orderInfo.vue'
  import ContactsTable from './components/contacts/contactsTable.vue'
  import BindCardModel from './components/bindCardModel.vue'
  import OrderHistoryModal from './components/orderHistoryModal.vue'
  import UserWorkModel from './components/userWorkModel.vue'
  import UploadContractModal from './components/UploadContractModal.vue'
  import { useModal } from '/@/components/Modal'
  import { usePermission } from '/@/hooks/web/usePermission'
  import { getOrderList, getQueryPhoneStatus } from '/@/api/order'
  import { getBindCardList } from '/@/api/sys/user'
  import { useMessage } from '/@/hooks/web/useMessage'

  const props = {
    detailInfo: { type: Object },
  }
  export default defineComponent({
    components: {
      Description,
      [Divider.name]: Divider,
      Image,
      OrderInfo,
      ContactsTable,
      BindCardModel,
      OrderHistoryModal,
      UserWorkModel,
      UploadContractModal,
    },
    props,
    setup(props) {
      const parent: any = inject('init')

      const isHistory = ref<boolean>(false)
      const bindCardList = ref<any>([])
      const detailInfo: any = ref(props.detailInfo)
      const personData = ref<any>({})

      const { createMessage } = useMessage()
      const { hasPermission } = usePermission()
      const [orderHistoryRegister, { openModal: openOrderHistoryModel }] = useModal()
      const [bindCardRegister, { openModal: openBindCardModel }] = useModal()
      const [userWorkRegister, { openModal: openUserWorkModel }] = useModal()
      const [uploadContractRegister, { openModal: openUploadContractModel }] = useModal()

      // 订单信息 - 订单总额
      detailInfo.value['platform'] = handlePlatform(detailInfo.value)
      function handlePlatform(data) {
        const find: any = platform.find((v) => v.value == data.sourceFrom)
        return find?.label || '无'
      }
      // 订单信息 - 增值服务
      detailInfo.value['service'] = handleService(detailInfo.value)
      function handleService(data) {
        const list = data.valueAddedServiceList
        let txt = ''
        if (list && list[0]) {
          const name = list[0]?.name || ''
          const money = list[0]?.money || 0
          const sum = formatNumber(money, 2, '.', ',', '￥', '元')
          txt = `${name} ${sum}`
        }
        return txt
      }

      // 用户基本信息
      personData.value = {
        ...detailInfo.value?.userV0,
        ...detailInfo.value?.userIdentifyVO,
        ...detailInfo.value?.riskVO,
        lipReadingDetectionList: detailInfo.value?.lipReadingDetectionList,
      }

      // 处理紧急联系人表格数据
      const contact = detailInfo.value?.userIdentifyVO || {}

      init()
      async function init() {
        // 获取银行卡列表
        getBindCardList({
          uid: contact.uid,
        }).then((res) => {
          bindCardList.value = res.alreadyCardList
        })

        const list = await getOrderList({
          idCard: contact.idCard,
          nickName: contact.nickName,
          orderIds: [detailInfo.value.id].join(','),
          statusList: [801, 901, 1101, 1201].join(','),
        })
        isHistory.value = !!list.total
      }
      function handleSuccess() {
        parent()
      }

      function openBindCardModals() {
        openBindCardModel(true, {
          isUpdate: true,
          bindCardList,
          record: {
            ...detailInfo.value,
          },
        })
      }

      function handleBindCard() {
        const status = detailInfo.value.status == 400
        if (!status) {
          openBindCardModals()
          return
        }

        const cardModal = Modal.warning({
          title: () => '用户未操作绑卡代扣, 请通知用户进行小程序绑卡',
          content: () =>
            h(
              'div',
              {
                style: { color: 'red', cursor: 'pointer' },
                onClick: () => {
                  cardModal.destroy()
                  openBindCardModals()
                },
              },
              '如用户无可绑定银行卡，请点击此处',
            ),
        })
      }

      function handleOrderListModal() {
        openOrderHistoryModel(true, {
          isUpdate: true,
          record: {
            ...contact,
            orderId: detailInfo.value.id,
          },
        })
      }

      async function handleUpdatePhoneStatus(phone: string) {
        try {
          await getQueryPhoneStatus({ phone, expSeconds: 0 })
          createMessage.success(`更新成功`)
          handleSuccess()
        } finally {
        }
      }

      function handleOrderUploadContractModal() {
        openUploadContractModel(true, {
          isUpdate: true,
          record: {
            ...detailInfo.value,
            orderId: detailInfo.value.id,
          },
        })
      }

      function handleUserWordModal(type: string) {
        openUserWorkModel(true, {
          isUpdate: true,
          record: {
            ...contact,
            orderId: detailInfo.value.id,
            type,
          },
        })
      }

      return {
        personSchema: personSchema({ handleUserWordModal, handleUpdatePhoneStatus }),
        personData,
        detailInfo,
        formatNumber,
        contact,
        handleBindCard,
        bindCardRegister,
        handleOrderListModal,
        orderHistoryRegister,
        hasPermission,
        isHistory,
        handleSuccess,
        userWorkRegister,
        handleOrderUploadContractModal,
        uploadContractRegister,
      }
    },
  })
</script>
<style lang="less" scoped>
  .desc-wrap {
    padding: 16px;
    background-color: @component-background;
  }

  .icon {
    margin-left: 10px;
    cursor: pointer;
    color: #3ca2fc;
  }

  .orderListBtn {
    position: absolute;
    top: 0;
    right: 10px;
  }
</style>
